windowlist.script:
					function onSortCompare(w1, w2)
						if w1.weaponsort.getValue() == "" then
							return true;
						elseif w2.weaponsort.getValue() == "" then
							return false;
						end
						return w1.weaponsort.getValue() > w2.weaponsort.getValue();
					end

					local bActiveLock = false
					function onActiveWeaponChanged(sChildNodeName)
						if not bActiveLock then
							for k, v in ipairs(getWindows()) do
								if (v.getDatabaseNode().getName() ~= sChildNodeName) then
									v.getDatabaseNode().getChild("color").setValue("ffffffff");
									v.getDatabaseNode().getChild("frame").setValue("dailyframe");
									v.weaponname.setFrame("dailyframe");
									v.weaponname.setColor("ffffffff");
									v.weaponisactive.setState(false);
									v.skillcheck.setVisible(false);
								else
									if v.weaponisactive.getState() == true then
										v.getDatabaseNode().getChild("color").setValue("ffffffff");
										v.getDatabaseNode().getChild("frame").setValue("dailyframe");
										v.weaponname.setFrame("dailyframe");
										v.weaponname.setColor("ffffffff");
										v.weaponisactive.setState(false);
										v.skillcheck.setVisible(false);
										local msg = "[Weapon-Change] Unarmed";
										ChatManager.Message(msg, "ooc");
										local weaponnode = v.getDatabaseNode().getNodeName();
										v.windowlist.window.getDatabaseNode().getChild("node.activeweapon").setValue("none");
									else
										v.getDatabaseNode().getChild("color").setValue("ffffffff");
										v.getDatabaseNode().getChild("frame").setValue("atwillframe");
										v.weaponname.setFrame("atwillframe");
										v.weaponname.setColor("ffffffff");
										v.weaponisactive.setState(true);
										v.skillcheck.onSourceUpdate();
										v.skillcheck.setVisible(true);
										local name = v.weaponname.getValue();
										local msg = "[Weapon-Change] (" .. name .. ")";
										ChatManager.Message(msg, "ooc");
										local weaponnode = v.getDatabaseNode().getNodeName();
										v.windowlist.window.getDatabaseNode().getChild("node.activeweapon").setValue(weaponnode);
									end
								end
							end

							bActiveLock = false;
						end
					end

					function onDrop(x, y, draginfo)
						local sDragType = draginfo.getType();
						if sDragType ~= "shortcut" then
							return false;
						end
						local sDropClass, sDropNodeName = draginfo.getShortcutData();
						if not StringManager.contains({ "referenceweapon"}, sDropClass) then
							return false;
						end
						local nodeSource = draginfo.getDatabaseNode();
						local sSourceType = DB.getValue(nodeSource, "subtype", "");
						local sSourceName = DB.getValue(nodeSource, "name", "");
						local sSourceCategory = DB.getValue(nodeSource, "category", "");
						local nSourceReach = DB.getValue(nodeSource, "reach", 0);
						local nSourceDamage = DB.getValue(nodeSource, "damage", 0);
						local nSourceDamageStrength = DB.getValue(nodeSource, "damagestrength", 0);
						local sSourceDamageType = DB.getValue(nodeSource, "damagetype", "");
						local nSourceAP = DB.getValue(nodeSource, "ap", 0);
						local nSourceRecoil = DB.getValue(nodeSource, "recoil", 0);
						local sSourceMode = DB.getValue(nodeSource, "mode", "");
						local nSourceAmmo = DB.getValue(nodeSource, "ammo", 0);
						local sSourceAmmoType = DB.getValue(nodeSource, "ammotype", "");

						local w = createWindow();
						w.weaponname.setValue(sSourceName);
						w.weaponsubtype.setStringValue(sSourceCategory);
						w.weaponreach.setValue(nSourceReach);
						w.weapondamage.setValue(nSourceDamage);
						if nSourceDamageStrength == 1 then
							w.weaponhasstrength.setState(true);
						else
							w.weaponhasstrength.setState(false);
						end
						if sSourceDamageType == "P" then
							w.weapondamagetype.setStringValue("Physical");
						elseif sSourceDamageType == "S" then
							w.weapondamagetype.setStringValue("Stun");
						elseif sSourceDamageType == "f" or  sSourceDamageType == "Pf" then
							w.weapondamagetype.setStringValue("Flechette");
						elseif sSourceDamageType == "S(e)" then
							w.weapondamagetype.setStringValue("Electricity");
						elseif sSourceDamageType == "t" then
							w.weapondamagetype.setStringValue("Toxic");
						elseif sSourceDamageType == "F" then
							w.weapondamagetype.setStringValue("Fire");
						elseif sSourceDamageType == "m" then
							w.weapondamagetype.setStringValue("Magic");
						elseif sSourceDamageType == "s" then
							w.weapondamagetype.setStringValue("Special");
						end
						w.weaponarmourpenetration.setValue(nSourceAP);
						w.weaponrecoil.setValue(nSourceRecoil);
						w.weaponrecoil.setReadOnly(true);
						w.availablemodes.setValue(sSourceMode);
						w.availablemodes.setReadOnly(true);
						local sFirstFireMode = string.sub (sSourceMode, 0, 2);
						if sFirstFireMode == "SS" then
							sFirstFireMode = "1";
						elseif sFirstFireMode == "SA" then
							sFirstFireMode = "2";
						elseif sFirstFireMode == "SB" then
							sFirstFireMode = "3";
						elseif sFirstFireMode == "LB" then
							sFirstFireMode = "4";
						elseif sFirstFireMode == "FA" then
							sFirstFireMode = "5";
						end
						w.sa.setStringValue(sFirstFireMode);
						w.weaponloadcapacity.setValue(nSourceAmmo);
						w.weaponmagazinetype.setStringValue(sSourceAmmoType);
						w.weaponsubtype.setReadOnly(true);
						w.weaponreach.setReadOnly(true);
						w.weapondamage.setReadOnly(true);
						w.weaponhasstrength.setReadOnly(true);
						w.weapondamagetype.setReadOnly(true);
						w.weaponarmourpenetration.setReadOnly(true);
						w.weaponloadcapacity.setReadOnly(true);
						w.weaponmagazinetype.setReadOnly(true);
						w.locked.setValue(1);
						w.skillcheck.onSourceUpdate();
						w.skillcheck.setReadOnly(true);

						w.shortcut.setValue(draginfo.getShortcutData());

						if sSourceType == "Firearms" then
							local w2 = w.list_ammunition.createWindow();
							if  w.weapondamagetype.getStringValue() == "Flechette" then
								w2.ammunitionname.setValue("Flechette Ammo");
								w2.ammodv.setValue(0);
								w2.ammodamagetype.setStringValue(w.weapondamagetype.getStringValue());
								w2.ammoarmourused.setStringValue("I");
								w2.ammoap.setValue(0);
							else
								w2.ammunitionname.setValue("Regular Ammo");
								w2.ammodv.setValue(0);
								w2.ammodamagetype.setStringValue(w.weapondamagetype.getStringValue());
								w2.ammoarmourused.setStringValue("B");
								w2.ammoap.setValue(0);
							end

							w2.locked.setValue(1);
							w2.ammocount.onDoubleClick();
							w2.ammunitioninusebox.onDoubleClick();

							w2.ammunitionname.setReadOnly(true);
							w2.ammodv.setReadOnly(true);
							w2.ammodamagetype.setReadOnly(true);
							w2.ammoarmourused.setReadOnly(true);
							w2.ammoap.setReadOnly(true);
						end

						return true;
					end

skillscore.script:
					function onDoubleClick()
						rWeaponNode = window.getDatabaseNode().getParent().getParent();

						local nCapacity = NodeManager.get(window.getDatabaseNode().getParent().getParent(), "weapon.loadcapacity", 0);
						self.setValue(nCapacity);

						local name = window.ammunitionname.getValue();

						local msg = "[Ammo-Reload] (" .. name .. " x " .. nCapacity .. ")";
						ChatManager.Message(msg, "ooc");

					end

checkbox.script:
function onDoubleClick()
	local a = window.getDatabaseNode().getName();
	window.windowlist.onActiveChanged(a);
end

button_toggle.script:
					function onInit()
						super.onInit();
						local nodeChar = window.getDatabaseNode();
						if nodeChar then
							local nodeAmmunitionList = nodeChar.createChild("ammunition","string");

							if nodeAmmunitionList then
								nodeAmmunitionList.onChildAdded = updateVisibility;
								nodeAmmunitionList.onChildUpdate = updateVisibility;
								updateVisibility(nodeAmmunitionList);
							else
								setVisible(false);
							end
						end
					end

					function updateVisibility(nodeAmmunitionList)
						if nodeAmmunitionList.getChildCount() > 0 then
							if not isVisible() then
								setValue(true);
							end
							setVisible(true);
						else
							setVisible(false);
							if getValue() then
								setValue(false);
							end
						end
					end

					function onValueChanged()
						window.toggleAmmunition();
					end

powerstring.script:
					function updateColor()
						window.weaponname.setColor(window.getDatabaseNode().getChild("color").getValue());
						window.weaponname.setFrame(window.getDatabaseNode().getChild("frame").getValue(), 8, 0, 8, 1)
					end

					function onInit()
						window.updateColorframe();
						updateColor();
						if not window.windowlist.window.getDatabaseNode().getChild("node.activeweapon") then
							window.windowlist.window.getDatabaseNode().createChild("node.activeweapon", "string").setValue("none");
						end
						local b = window.windowlist.window.getDatabaseNode().getChild("node.activeweapon").getValue();
					end

					function onDoubleClick()
						local a = window.getDatabaseNode().getName();
						window.windowlist.onActiveWeaponChanged(a);
					end

