button_stringcycler.script:
					function onValueChanged()
						updateWeaponTypeVisibility();
					end

					function onInit()
						if not window.getDatabaseNode().getChild("weapontype") then
							window.getDatabaseNode().createChild("weapontype", "string");
						end
						if not window.getDatabaseNode().getChild("weaponsubtype") then
							window.getDatabaseNode().createChild("weaponsubtype", "string");
						end
					    super.onInit();
					    updateWeaponTypeVisibility();
					end

					function getRange(sRange)
						local mult = 1;
						local nRange = 0;
						local strengthNode = window.getDatabaseNode().getParent().getParent();
						if sRange == "STR" then
							nRange = NodeManager.get(strengthNode, "base.attribute.strength.check", 1);
						elseif string.match(sRange, "STRx*", 0) then
							nRange = NodeManager.get(strengthNode, "base.attribute.strength.check", 1);
							local sMult = string.sub(sRange, 5);
							mult = tonumber(sMult);
						else
							nRange = sRange;
						end
						return nRange * mult;
					end

					function updateWeaponTypeVisibility()
						local b = window.getDatabaseNode().getChild("weaponsubtype").getValue();
						local a = b;
						local rWeaponType = DataCommon.weapondata[b];
						if rWeaponType then
							local dbNode = window.getDatabaseNode().getChild("weapon");

							window.minimumrange.setValue(getRange(rWeaponType.min));
							window.shortrange.setValue(getRange(rWeaponType.short));
							window.mediumrange.setValue(getRange(rWeaponType.medium));
							window.longrange.setValue(getRange(rWeaponType.long));
							window.extremerange.setValue(getRange(rWeaponType.extreme));
							a = rWeaponType.type;
						end
						window.getDatabaseNode().getChild("weapontype").setValue(a);

						if a == "Melee" then
							window.weaponarmourpenetration.setVisible(true);
							window.ap.setVisible(true)
							window.weaponrecoil.setVisible(false);
							window.weaponmagazinetype.setVisible(false);
							window.rc.setVisible(false);
							window.weaponloadcapacity.setVisible(false);
							window.sa.setVisible(false);
							window.bursttype.setVisible(false);
							window.minimumrange.setVisible(false);
							window.shortrange.setVisible(false);
							window.mediumrange.setVisible(false);
							window.longrange.setVisible(false);
							window.extremerange.setVisible(false);
							window.ranges.setVisible(false);
							window.weaponmagazinetype.setVisible(false);
							window.weaponreach.setVisible(true);
							window.labelreach.setVisible(true);
							window.weaponhasstrength.setVisible(true);
							window.labelhasstrength.setVisible(true);
							window.weaponstrengthbonus.setVisible(true);

						elseif a == "Missile" or a == "Thrown" then
							window.weaponarmourpenetration.setVisible(true);
							window.ap.setVisible(true)
							window.weaponrecoil.setVisible(false);
							window.weaponmagazinetype.setVisible(false);
							window.rc.setVisible(false);
							window.weaponloadcapacity.setVisible(false);
							window.sa.setVisible(false);
							window.bursttype.setVisible(false);
							window.minimumrange.setVisible(false);
							window.shortrange.setVisible(false);
							window.mediumrange.setVisible(false);
							window.longrange.setVisible(false);
							window.extremerange.setVisible(false);
							window.ranges.setVisible(false);
							window.weaponmagazinetype.setVisible(false);
							window.weaponreach.setVisible(false);
							window.labelreach.setVisible(false);
							window.weaponhasstrength.setVisible(true);
							window.labelhasstrength.setVisible(true);
							window.weaponstrengthbonus.setVisible(true);

						elseif a == "Firearm" then
							window.weaponarmourpenetration.setVisible(true);
							window.ap.setVisible(true)
							window.weaponrecoil.setVisible(true);
							window.weaponmagazinetype.setVisible(false);
							window.rc.setVisible(true);
							window.weaponloadcapacity.setVisible(true);
							window.sa.setVisible(true);
							window.bursttype.setVisible(true);
							window.minimumrange.setVisible(false);
							window.shortrange.setVisible(false);
							window.mediumrange.setVisible(false);
							window.longrange.setVisible(false);
							window.extremerange.setVisible(false);
							window.ranges.setVisible(false);
							window.weaponmagazinetype.setVisible(true);
							window.weaponreach.setVisible(false);
							window.labelreach.setVisible(false);
							window.weaponhasstrength.setVisible(false);
