 					function onInit()
 						super.onInit();
						weaponStrengthBonus();
					end

					function onValueChanged()
						weaponStrengthBonus();
					end

					function weaponStrengthBonus()
						local a = self.getState();
						if a == false then
							window.getDatabaseNode().getChild("weapon.strengthbonus").setValue("0");
						else
							local b = math.ceil(window.windowlist.window.getDatabaseNode().getChild("base.attribute.strength.check").getValue() /2);
							window.getDatabaseNode().getChild("weapon.strengthbonus").setValue(b);
						end
					end

windowlist.script:
					function onSortCompare(w1, w2)
						return w1.order.getValue() > w2.order.getValue();
					end

					function reset()
						for k,v in pairs(getWindows()) do
							v.getDatabaseNode().delete();
						end
					end

					function getOrder(node)
						if NodeManager.get(node, "order", 0) == 0 then
							local ordertable = {};
							for k,v in pairs(getDatabaseNode().getChildren()) do
								ordertable[NodeManager.get(v, "order", 0)] = true;
							end

							local i = 1;
							while ordertable[i] do
								i = i + 1;
							end

							NodeManager.set(node, "order", "number", i);
						end
					end
					local bActiveLock = false
					function onActiveChanged(sChildNodeName)

						if not bActiveLock then
							for k, v in ipairs(getWindows()) do
								if (v.getDatabaseNode().getName() ~= sChildNodeName) then
									v.ammunitioninusebox.setState(false);
								else
									v.ammunitioninusebox.setState(true);
									local f = v.getDatabaseNode().getParent().getParent().getChild("frame").getValue();
									if f == "atwillframe" then
										local name = v.ammunitionname.getValue();
										local msg = "[Ammo-Change] (" .. name .. ")";
										ChatManager.Message(msg, "ooc");
									end
									local ammonode = v.getDatabaseNode().getNodeName();
									if not v.windowlist.window.getDatabaseNode().getChild("activeammo") then
									v.windowlist.window.getDatabaseNode().createChild("activeammo", "string");
									end
									v.windowlist.window.getDatabaseNode().getChild("activeammo").setValue(ammonode);
								end
							end

							bActiveLock = false;
						end
					end
