string_ct.script:
					function onValueChanged()
						if self.update then
							self.update();
						end

							if linknode and not isReadOnly() then
							if not locked then
								locked = true;
								linknode.setValue(getValue());
								locked = false;
							end
						end
						local msg = ""
						if self.getValue() == "" or self.getValue() == "0" then
							window.getDatabaseNode().getChild("initbase").setValue(window.getDatabaseNode().getChild("ctbaseini").getValue());
							window.getDatabaseNode().getChild("passbase").setValue(window.getDatabaseNode().getChild("ctbaseinipass").getValue());
						elseif self.getValue() == "1" then
							window.getDatabaseNode().getChild("initbase").setValue(window.getDatabaseNode().getChild("ctbaseastini").getValue());
							window.getDatabaseNode().getChild("passbase").setValue(window.getDatabaseNode().getChild("ctbaseastinipass").getValue());
						elseif self.getValue() == "2" then
							window.getDatabaseNode().getChild("initbase").setValue(window.getDatabaseNode().getChild("ctbasematini").getValue());
							window.getDatabaseNode().getChild("passbase").setValue(window.getDatabaseNode().getChild("ctbasematinipass").getValue());
						elseif self.getValue() == "3" then
							window.getDatabaseNode().getChild("initbase").setValue(window.getDatabaseNode().getChild("ctbasehotmatini").getValue());
							window.getDatabaseNode().getChild("passbase").setValue(window.getDatabaseNode().getChild("ctbasehotmatinipass").getValue());
						end
					end

script.script:
					function onInit()
						if super.onInit then
							super.onInit();
						end

						if self.update then
							self.update();
						end
						local result = window.getDatabaseNode().getChild("initbase").getValue() + window.getDatabaseNode().getChild("initroll").getValue() + window.getDatabaseNode().getChild("initmod").getValue();
						if result then
							self.setValue(result);
						end
					end

					function update()
						window.windowlist.applySort();
					end

